IN vec2 texCoord;
uniform vec2 uRes;
uniform vec2 uPoint;
uniform vec3 uColor;
uniform float uRadius;
uniform float uAspectRatio;

uniform sampler2D ColorInputTex;


{{MODULES_HEAD}}
void main()
{
    vec2 vUV = gl_FragCoord.xy/vec2(uRes.x,uRes.y);

    vec2 p = vUV.xy - uPoint;
    p.x *= uAspectRatio;

    vec3 splat = exp(-dot(p,p) / uRadius) * uColor;
    vec3 base = texture(ColorInputTex, vUV.st).rgb;

    vec4 col= vec4(base + splat, 1.0);
    {{MODULE_COLOR}}
    outColor = col;
}

