IN vec2 texCoord;
uniform vec2 uRes;

uniform sampler2D VelocityInputTex;


{{MODULES_HEAD}}
void main()
{
    vec2 vUV = gl_FragCoord.xy/vec2(uRes.x,uRes.y);

    ivec2 viUV = ivec2(gl_FragCoord.xy);

    ivec2 pL = ivec2(viUV.x - 1, viUV.y);
    ivec2 pR = ivec2(viUV.x + 1, viUV.y);
    ivec2 pT = ivec2(viUV.x, viUV.y + 1);
    ivec2 pB = ivec2(viUV.x, viUV.y - 1);

    float L = texelFetch(VelocityInputTex, pL, 0).x;
    float R = texelFetch(VelocityInputTex, pR, 0).x;
    float T = texelFetch(VelocityInputTex, pT, 0).x;
    float B = texelFetch(VelocityInputTex, pB, 0).x;

    float curl = R - L - T + B;

    vec4 col= vec4(0.5 * curl, 0.0, 0.0, 1.0);
    {{MODULE_COLOR}}
    outColor = col;
}

