IN vec2 texCoord;
uniform vec2 uRes;

uniform float uDelta;
uniform float uVorticity;

uniform sampler2D VelocityInputTex;
uniform sampler2D CurlInputTex;



{{MODULES_HEAD}}
void main()
{
    vec2 vUV = gl_FragCoord.xy/vec2(uRes.x,uRes.y);
    ivec2 viUV = ivec2(gl_FragCoord.xy);

    ivec2 pL = ivec2(viUV.x - 1, viUV.y);
    ivec2 pR = ivec2(viUV.x + 1, viUV.y);
    ivec2 pT = ivec2(viUV.x, viUV.y + 1);
    ivec2 pB = ivec2(viUV.x, viUV.y - 1);

    float L = texelFetch(CurlInputTex, pL, 0).x;
    float R = texelFetch(CurlInputTex, pR, 0).x;
    float T = texelFetch(CurlInputTex, pT, 0).x;
    float B = texelFetch(CurlInputTex, pB, 0).x;
    float C = texelFetch(CurlInputTex, ivec2(viUV), 0).x;

    vec2 force = 0.5 * vec2(abs(T) - abs(B), abs(R) - abs(L));
    force /= length(force) + 0.0001;
    force *= uVorticity * C;
    force.y *= -1.0;

    vec2 vel = texelFetch(VelocityInputTex, ivec2(viUV), 0).xy;

    vec4 col= vec4(vel + force * uDelta, 0.0, 1.0);
    {{MODULE_COLOR}}
    outColor = col;
}

