IN vec2 texCoord;
uniform vec2 uRes;


uniform sampler2D vorticityInputTex;



{{MODULES_HEAD}}
void main()
{
    vec2 vUV = gl_FragCoord.xy/vec2(uRes.x,uRes.y);

    vec2 pL = vec2(vUV.x - 1. / uRes.x, vUV.y);
    vec2 pR = vec2(vUV.x + 1. / uRes.x, vUV.y);
    vec2 pT = vec2(vUV.x, vUV.y + 1. / uRes.y);
    vec2 pB = vec2(vUV.x, vUV.y - 1. / uRes.y);

    float L = texture(vorticityInputTex, pL).x;
    float R = texture(vorticityInputTex, pR).x;
    float T = texture(vorticityInputTex, pT).y;
    float B = texture(vorticityInputTex, pB).y;

    vec2 C = texture(vorticityInputTex, vUV).xy;

    if (pL.x < 0.0) { L = -C.x; }
    if (pR.x > 1.0) { R = -C.x; }
    if (pT.y > 1.0) { T = -C.y; }
    if (pB.y < 0.0) { B = -C.y; }

    float divergence = 0.5 * (R - L + T - B);

    vec4 col= vec4(divergence, 0.0, 0.0, 1.0);
    {{MODULE_COLOR}}
    outColor = col;
}

