uniform sampler2D ClearPressureInputTex;
uniform sampler2D DivergenceInputTex;



{{MODULES_HEAD}}
void main()
{
    vec2 vUV = gl_FragCoord.xy/vec2(uRes.x,uRes.y);

    vec2 pL = vec2(vUV.x - 1.5 / uRes.x, vUV.y);
    vec2 pR = vec2(vUV.x + 1.5 / uRes.x, vUV.y);
    vec2 pT = vec2(vUV.x, vUV.y + 1.5 / uRes.y);
    vec2 pB = vec2(vUV.x, vUV.y - 1.5 / uRes.y);

    float L = texture(ClearPressureInputTex, pL).x;
    float R = texture(ClearPressureInputTex, pR).x;
    float T = texture(ClearPressureInputTex, pT).x;
    float B = texture(ClearPressureInputTex, pB).x;

    float divergence = texture(DivergenceInputTex, vUV.xy).x;
    float pressure = (L + R + B + T - divergence) * 0.25;

    vec4 col= vec4(pressure, 0.0, 0.0, 1.0);
    {{MODULE_COLOR}}
    outColor = col;
}

